import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ArtistData } from '../../data/artist-data';
import { TrackData } from '../../data/track-data';
import { AlbumData } from '../../data/album-data';
import { SpotifyService } from '../../services/spotify.service';


@Component({
  selector: 'app-album-page',
  templateUrl: './album-page.component.html',
  styleUrls: ['./album-page.component.css']
})
export class AlbumPageComponent implements OnInit {
  albumId:string;
  album:AlbumData;
  tracks:TrackData[];


  constructor(private route: ActivatedRoute, private spotifyService:SpotifyService) { }


  //TODO: inject spotifyService and use it to get the album data and the tracks for the album
  //when the album page is activated, sends requests to Express to get data to populate the appropriate fields
  ngOnInit() {
    this.albumId = this.route.snapshot.paramMap.get('id');

    this.spotifyService.getAlbum(this.albumId).then((data) => {
      this.album = data;
    })


    this.spotifyService.getTracksForAlbum(this.albumId).then((data) => {
      this.tracks = data;
    })
  }


}
<div id = "wholePage" *ngIf="album" >
    <div id = "albumPic">
        <h1>{{album.name}}</h1>
        <img id="albumPicImg" [src]="this.album.imageURL">
        <p>
            <li *ngFor="let artist of this.album.artists">
                Artist: <a href="./artist/{{artist.id}}">{{artist.name}}</a>
            </li>
        </p>
        <p>
            <a class="btn btn-light" role="button" target="_blank" [href]="this.album.url">Open {{album.name}} on Spotify</a>
        </p>
    </div>
    <div id = "albumTracks">
        <h3>Album Tracks</h3>
        <app-track-list id ="albumTracksRows" [tracks]="this.tracks" hideArtist="true" hideAlbum="true"></app-track-list>
    </div>
</div>
