import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ArtistData } from '../../data/artist-data';
import { TrackData } from '../../data/track-data';
import { AlbumData } from '../../data/album-data';
import { TrackFeature } from '../../data/track-feature';
import { SpotifyService } from '../../services/spotify.service';

@Component({
  selector: 'app-track-page',
  templateUrl: './track-page.component.html',
  styleUrls: ['./track-page.component.css']
})
export class TrackPageComponent implements OnInit {
	trackId:string;
	track:TrackData;
  audioFeatures:TrackFeature[];

  constructor(private route: ActivatedRoute, private spotifyService:SpotifyService) { }

  ngOnInit() {
  	this.trackId = this.route.snapshot.paramMap.get('id');
  	//TODO: Inject the spotifyService and use it to get the track data and it's audio features

    //calls the Express request function in the service module to populate the track and audioFeatures fields
    this.spotifyService.getTrack(this.trackId).then((data) => {
      this.track = data;
    })

    this.spotifyService.getAudioFeaturesForTrack(this.trackId).then((data) => {
      this.audioFeatures = data;
    })
  }

}
<div id="wholePage">
	<div id="trackSection" *ngIf="track">
		<h1>{{track.name}}</h1>
		<p>
			Track on <a href="./album/{{track.album.id}}">{{this.track.album.name}}</a>
		</p>
		<div>
			<p *ngFor="let artist of this.track.album.artists">
				Artist: <a href="./artist/{{artist.id}}">{{artist.name}}</a>
			</p>
		</div>
		<p>
			Duration: {{track.durationStr}}
		</p>
		<p>
			<a class="btn btn-light" role="button" target="_blank" [href]="this.track.url">Open {{track.name}} on Spotify</a>
		</p>
	</div>
	<div id="keywordsSection" *ngIf="audioFeatures" >
		<p *ngFor="let feature of audioFeatures">
			<app-thermometer [featureType]="feature.name" [percentString]="feature.percentageString" [color]="feature.color">
			</app-thermometer>
		</p>
	</div>
</div>
